mapper.select:
SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.* FROM COACH A
WHERE
1=1

    AND (UPPER(A.SUBJECT) like '%'||UPPER(#{searchVal})||'%' OR UPPER(A.TITLE) like '%'||UPPER(#{searchVal})||'%')


    AND ( TO_CHAR(A.REG_DT, 'YYYY-MM-DD') BETWEEN #{searchSDate} AND #{searchEDate} )

ORDER BY A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

mapper.insert:

    SELECT nvl(MAX(COACH_IDX),0)+1 AS COACH_IDX  FROM COACH

INSERT INTO COACH (
COACH_IDX
,SUBJECT
,TITLE
,WEEK_TYPE
,START_TIME
,END_TIME
,URL
,REG_ID
,REG_DT
) VALUES (
#{coach_idx}
,#{subject}
,#{title}
,#{week_type}
,#{start_time}
,#{end_time}
,#{url}
,#{reg_id}
,SYSDATE
)

mapper.update:
UPDATE COACH SET
SUBJECT = #{subject}
,TITLE = #{title}
,WEEK_TYPE = #{week_type}
,START_TIME = #{start_time}
,END_TIME = #{end_time}
,URL = #{url}
,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE COACH_IDX = #{coach_idx}

UPDATE COACH_ROOM SET
START_TIME = #{start_time}
,END_TIME = #{end_time}
,URL = #{url}
,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE COACH_ROOM_IDX = #{coach_room_idx}
