mapper.update:
UPDATE ATCH_FILE_DTL SET
 	FILE_STORE_PATH	= #{file_store_path}
	,FILE_NM 		= #{file_nm}
	,FILE_ORI_NM	= #{file_ori_nm}
	,FILE_EXT		= #{file_ext}
	,FILE_SIZE		= #{file_size}
	,FILE_CON		= #{file_con}
	,FILE_EXP		= #{file_exp}
	,FILE_SRC		= #{file_src}
WHERE
	ATCH_FILE_ID = #{atch_file_id}
 	AND
 	FILE_NUM = #{file_num}

UPDATE ATCH_FILE_DTL SET
	FILE_STORE_PATH	= #{file_store_path}
   ,FILE_NM 		= #{file_nm}
   ,FILE_ORI_NM	= #{file_ori_nm}
   ,FILE_EXT		= #{file_ext}
   ,FILE_SIZE		= #{file_size}
   ,FILE_CON		= #{file_con}
   ,FILE_EXP		= #{file_exp}
   ,FILE_SRC		= #{file_src}
   ,FILE_NUM		= #{file_num_tmp}
WHERE
	ATCH_FILE_ID = #{atch_file_id}
  AND
	FILE_NUM = #{file_num}

mapper.delete:
    DELETE FROM ATCH_FILE_DTL
	WHERE
		ATCH_FILE_ID = #{atch_file_id}
	AND
		FILE_NUM = #{file_num}
