mapper.select:
SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.* FROM MAIN_IMG A
WHERE
1=1

    AND A.KIND = #{searchKind}


    AND A.USE_YN = #{searchKind2}


    AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')

ORDER BY A.USE_YN DESC, A.ORD ASC, A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

SELECT COUNT(*) FROM MAIN_IMG A
WHERE
1=1

    AND A.KIND = #{searchKind}


    AND A.USE_YN = #{searchKind2}


    AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')

SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.* FROM POPUP A
WHERE
1=1

    AND A.KIND = #{searchKind}


    AND A.USE_YN = #{searchKind2}


    AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')

ORDER BY A.USE_YN DESC, A.ORD ASC, A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

SELECT COUNT(*) FROM POPUP A
WHERE
1=1

    AND A.KIND = #{searchKind}


    AND A.USE_YN = #{searchKind2}


    AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')

mapper.insert:

    SELECT nvl(MAX(MAIN_IMG_IDX),0)+1 AS MAIN_IMG_IDX  FROM MAIN_IMG

INSERT INTO MAIN_IMG (
MAIN_IMG_IDX
,KIND
,NAME
,USE_YN
,ORD
,ATCH_FILE_ID
,ALT_TXT
,LINK
,LINK_TGT
,REG_ID
,REG_DT
) VALUES (
#{main_img_idx}
,#{kind}
,#{name}
,#{use_yn}
,'1'
,#{atch_file_id}
,#{alt_txt}
,#{link}
,#{link_tgt}
,#{reg_id}
,SYSDATE
)


    SELECT nvl(MAX(POPUP_IDX),0)+1 AS POPUP_IDX  FROM POPUP

INSERT INTO POPUP (
POPUP_IDX
,KIND
,NAME
,USE_YN
,ORD
,START_DT
,END_DT
,ATCH_FILE_ID
,ALT_TXT
,LINK
,LINK_TGT
,REG_ID
,REG_DT
) VALUES (
#{popup_idx}
,#{kind}
,#{name}
,#{use_yn}
,#{ord}
,TO_DATE(#{start_dt}, 'YYYY-MM-DD hh24:mi')
,TO_DATE(#{end_dt}, 'YYYY-MM-DD hh24:mi')
,#{atch_file_id}
,#{alt_txt}
,#{link}
,#{link_tgt}
,#{reg_id}
,SYSDATE
)

mapper.update:
UPDATE MAIN_IMG SET
KIND = #{kind}
,NAME = #{name}
,USE_YN = #{use_yn}
,ORD = #{ord}
,ATCH_FILE_ID = #{atch_file_id}
,ALT_TXT = #{alt_txt}
,LINK = #{link}
,LINK_TGT = #{link_tgt}
,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE MAIN_IMG_IDX = #{main_img_idx}

UPDATE POPUP SET
KIND = #{kind}
,NAME = #{name}
,USE_YN = #{use_yn}
,ORD = #{ord}
,START_DT = TO_DATE(#{start_dt}, 'YYYY-MM-DD hh24:mi')
,END_DT = TO_DATE(#{end_dt}, 'YYYY-MM-DD hh24:mi')
,ATCH_FILE_ID = #{atch_file_id}
,ALT_TXT = #{alt_txt}
,LINK = #{link}
,LINK_TGT = #{link_tgt}
,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE POPUP_IDX = #{popup_idx}
