mapper.select:
SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.*, G.NAME GRP_NAME FROM REQUEST A, GROUP_INFO G
WHERE
1=1 AND A.GROUP_IDX = G.GROUP_IDX

    AND A.GBN_CD = #{searchKind}


    AND A.WORK_TYPE = #{searchKind2}


    AND (TO_CHAR(A.REG_DT, 'YYYY-MM-DD') BETWEEN #{searchSDate} AND #{searchEDate})


    AND (UPPER(A.TITLE) like '%'||UPPER(#{searchVal})||'%')

ORDER BY A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

SELECT COUNT(*) FROM REQUEST A, GROUP_INFO G WHERE 1=1 AND A.GROUP_IDX = G.GROUP_IDX
AND A.GBN_CD = #{searchKind}
AND A.WORK_TYPE = #{searchKind2}
AND (TO_CHAR(A.REG_DT, 'YYYY-MM-DD') BETWEEN #{searchSDate} AND #{searchEDate})
AND (UPPER(A.TITLE) like '%'||UPPER(#{searchVal})||'%')

mapper.insert:

    SELECT nvl(MAX(REQ_IDX),0)+1 AS REQ_IDX  FROM REQUEST

INSERT INTO REQUEST (
REQ_IDX
,GROUP_IDX
,GBN_CD
,WORK_TYPE
,REG_NM
,TEL_NO
,URL
,TITLE
,CONT
,ATCH_FILE_ID
,CNT
,REG_ID
,REG_DT
) VALUES (
#{req_idx}
,#{group_idx}
,#{gbn_cd}
,#{work_type}
,#{reg_nm}
,#{tel_no}
,#{url}
,#{title}
,#{cont}
,#{atch_file_id}
,0
,#{reg_id}
,SYSDATE
)

mapper.update:
UPDATE REQUEST SET
    CNT = CNT + 1
WHERE REQ_IDX = #{req_idx}

UPDATE REQUEST SET
GBN_CD = #{gbn_cd}
,WORK_TYPE = #{work_type}
,TEL_NO = #{tel_no}
,URL = #{url}
,TITLE = #{title}
,CONT = #{cont}

    ,ATCH_FILE_ID = #{atch_file_id}

,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE REQ_IDX = #{req_idx}
