mapper.select:
SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.*, (SELECT CD_VAL FROM CODE WHERE CODE_IDX = A.CODE_IDX) AS CODE_NM FROM CLUB A
WHERE
1=1

    AND A.CODE_IDX = #{searchKind2}


    AND A.STATUS = #{searchKind3}


    AND TO_CHAR(A.REG_DT, 'YYYY-MM-DD') BETWEEN #{searchSDate} AND #{searchEDate}


    AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')


    AND A.REG_ID = #{searchVal8}

ORDER BY A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

SELECT COUNT(*) FROM CLUB A
WHERE
1=1

    AND A.CODE_IDX = #{searchKind2}


    AND A.STATUS = #{searchKind3}


    AND TO_CHAR(A.REG_DT, 'YYYY-MM-DD') BETWEEN #{searchSDate} AND #{searchEDate}


    AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')


    AND A.REG_ID = #{searchVal8}

SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.* FROM CLUB_NEWS A
WHERE
1=1 AND A.USE_YN = 'Y'



            AND (UPPER(A.TITLE) like '%'||UPPER(#{searchVal})||'%')


            AND (UPPER(A.CONT) like '%'||UPPER(#{searchVal})||'%')


            AND (UPPER(A.TITLE) like '%'||UPPER(#{searchVal})||'%' OR UPPER(A.CONT) like '%'||UPPER(#{searchVal})||'%')



ORDER BY A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.*, G.P_NAME P_NAME, G.NAME GRP_NAME, (SELECT CD_VAL FROM CODE WHERE CODE_IDX = A.CODE_IDX) AS CODE_NM FROM CLUB_STATUS A, GROUP_INFO G
WHERE
1=1 AND A.GROUP_IDX = G.GROUP_IDX

    AND A.CODE_IDX = #{searchKind}


    AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')

ORDER BY A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

SELECT COUNT(*) FROM CLUB_STATUS A, GROUP_INFO G WHERE 1=1 AND A.GROUP_IDX = G.GROUP_IDX
AND A.CODE_IDX = #{searchKind}
AND (UPPER(A.NAME) like '%'||UPPER(#{searchVal})||'%')

mapper.insert:

    SELECT nvl(MAX(CLUB_NEWS_IDX),0)+1 AS CLUB_NEWS_IDX  FROM CLUB_NEWS

INSERT INTO CLUB_NEWS (
CLUB_NEWS_IDX
,GROUP_IDX
,USE_YN
,TITLE
,CONT
,THUMB_ATCH_FILE_ID
,ATCH_FILE_ID
,CNT
,REG_ID
,REG_DT
) VALUES (
#{club_news_idx}
,#{group_idx}
,#{use_yn}
,#{title}
,#{cont}
,#{thumb_atch_file_id}
,#{atch_file_id}
,0
,#{reg_id}
,SYSDATE
)


    SELECT nvl(MAX(CLUB_STATUS_IDX),0)+1 AS CLUB_STATUS_IDX  FROM CLUB_STATUS

INSERT INTO CLUB_STATUS (
CLUB_STATUS_IDX
,GROUP_IDX
,CODE_IDX
,NAME
,STAND_DT
,ORG_NAME
,MEM_CNT
,MEET_DT
,INTRO
,ACTION
,REG_ID
,REG_DT
) VALUES (
#{club_status_idx}
,#{group_idx}
,#{code_idx}
,#{name}
,#{stand_dt}
,#{org_name}
,#{mem_cnt}
,#{meet_dt}
,#{intro}
,#{action}
,#{reg_id}
,SYSDATE
)


    SELECT nvl(MAX(CLUB_IDX),0)+1 AS CLUB_IDX FROM CLUB

INSERT INTO CLUB (
CLUB_IDX
,CODE_IDX
,STATUS
,NAME
,APY_NAME
,APY_MOB_NO
,STAND_DT
,MEM_CNT
,ORG_NAME
,MEET_DT
,MEET_PLC
,INTRO
,ACTION
,ATCH_FILE_ID
,MEMO
,REG_ID
,REG_DT
) VALUES (
#{club_idx}
,#{code_idx}
,'R'
,#{name}
,#{apy_name}
,#{apy_mob_no}
,TO_DATE(#{stand_dt}, 'YYYY-MM-DD')
,#{mem_cnt}
,#{org_name}
,#{meet_dt}
,#{meet_plc}
,#{intro}
,#{action}
,#{atch_file_id}
,#{memo}
,#{reg_id}
,SYSDATE
)

mapper.update:
UPDATE CLUB_NEWS SET
	CNT = CNT + 1
WHERE CLUB_NEWS_IDX = #{club_news_idx}

UPDATE CLUB SET
STATUS = #{status}

    ,MEMO = #{memo}

,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE CLUB_IDX = #{club_idx}

UPDATE CLUB_NEWS SET
USE_YN = #{use_yn}
,TITLE = #{title}
,CONT = #{cont}
,ATCH_FILE_ID = #{atch_file_id}

    ,THUMB_ATCH_FILE_ID = #{thumb_atch_file_id}

,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE CLUB_NEWS_IDX = #{club_news_idx}

UPDATE CLUB_STATUS SET
CODE_IDX = #{code_idx}
,NAME = #{name}
,STAND_DT = #{stand_dt}
,ORG_NAME = #{org_name}
,MEM_CNT = #{mem_cnt}
,MEET_DT = #{meet_dt}
,INTRO = #{intro}
,ACTION = #{action}
,UP_ID	= #{up_id}
,UP_DT = SYSDATE
WHERE CLUB_STATUS_IDX = #{club_status_idx}

UPDATE CLUB SET
UP_ID	= #{up_id}
,APY_MOB_NO = #{apy_mob_no}
,CODE_IDX = #{code_idx}
,NAME = #{name}
,STAND_DT = TO_DATE(#{stand_dt}, 'YYYY-MM-DD')
,ORG_NAME = #{org_name}
,MEM_CNT = #{mem_cnt}
,MEET_DT = #{meet_dt}
,MEET_PLC = #{meet_plc}
,INTRO = #{intro}
,ACTION = #{action}

    ,ATCH_FILE_ID = #{atch_file_id}

,UP_DT = SYSDATE
WHERE CLUB_IDX = #{club_idx}
