mapper.select:
SELECT * FROM (
SELECT ROWNUM AS RNUM, T.* FROM (
SELECT A.* FROM LAWTAX_COUNSEL A
WHERE
1=1

    AND A.GBN = #{searchKind2}


    AND A.STATUS = #{searchKind}


    AND (UPPER(A.TITLE) like '%'||UPPER(#{searchVal})||'%')


    AND ( TO_CHAR(A.REG_DT, 'YYYY-MM-DD') BETWEEN #{searchSDate} AND #{searchEDate})




            AND ( A.APY_ID = #{searchVal3} )



ORDER BY A.REG_DT DESC
) T WHERE ROWNUM <=#{lastIndex}
) WHERE RNUM >=#{firstIndex}

SELECT COUNT(*) FROM LAWTAX_COUNSEL A WHERE 1=1
AND A.GBN = #{searchKind2}
AND A.STATUS = #{searchKind}
AND (UPPER(A.TITLE) like '%'||UPPER(#{searchVal})||'%')
AND ( TO_CHAR(A.REG_DT, 'YYYY-MM-DD') BETWEEN #{searchSDate} AND #{searchEDate})
AND ( A.APY_ID = #{searchVal3} )

mapper.insert:

    SELECT nvl(MAX(LAWTAX_IDX),0)+1 AS LAWTAX_IDX FROM LAWTAX_COUNSEL

INSERT INTO LAWTAX_COUNSEL (
LAWTAX_IDX
,GBN
,TITLE
,CONT
,STATUS
,APY_ID
,APY_NM
,APY_MOB_NO
,APY_EMAIL
,APY_ADDR
,REG_ID
,REG_DT
) VALUES (
#{lawtax_idx}
,#{gbn}
,#{title}
,#{cont}
,'A'
,#{apy_id}
,#{apy_nm}
,#{apy_mob_no}
,#{apy_email}
,#{apy_addr}
,#{reg_id}
,SYSDATE
)

mapper.update:
		UPDATE LAWTAX_COUNSEL SET
        TITLE = #{title}
        ,CONT = #{cont}
        ,UP_ID	= #{up_id}
        ,UP_DT = SYSDATE
		WHERE LAWTAX_IDX = #{lawtax_idx}
